mapper.select:
select

from monitor_queue
where 1=1

  and queue_name=#{queueName,jdbcType=VARCHAR}


  and factory_name=#{factoryName,jdbcType=VARCHAR}


  and warn_msg_format=#{warnMsgFormat,jdbcType=VARCHAR}


  and warn_msg=#{warnMsg,jdbcType=VARCHAR}


  and warn_who=#{warnWho,jdbcType=VARCHAR}


  and warn_way=#{warnWay,jdbcType=VARCHAR}


  and warn_threshold=#{warnThreshold,jdbcType=INTEGER}
